[h: indexChosen=macro.args]

[h:itemReadied=json.get(getProperty("inventory"),indexChosen)]
[h:readiedID=json.get(itemReadied,"ID")]
[if(json.contains(itemReadied,"shieldDB")),h:
   readiedShieldDB=json.get(itemReadied,"shieldDB");
   readiedShieldDB=0]

<!-- Prepare output text. -->
[h:name=json.get(itemReadied,"name")]
[if(endsWith(name,"s") || indexOf(name, "armor") >= 0), h: 
     msg="a reliable set of " + json.get(itemReadied,"name") +".";
     msg="a trusty " + json.get(itemReadied,"name") +"."]

 <!-- If the item is ready, make it unready and set it aside without relinquishing ownership.
         If carried, add it to the items readied and remove it from the items carried.   
         If the item is neither carried nor readied, add it to the items carried. 
         In the symbolism of the inventory list,   to c to R to  to c to R ...    -->

[if(json.contains(itemsReadied, readiedID)),r,code:{
  [h:itemsReadied=json.remove(itemsReadied,json.indexOf(itemsReadied,readiedID))]
  [h: setProperty("shieldDB",getProperty("shieldDB")-readiedShieldDB)]
  [r:"sets aside " + msg]
};{
  [if(json.contains(itemsCarried, readiedID)),r,code:{
    [h:itemsReadied=json.append(itemsReadied,readiedID)]
    [h:itemsCarried=json.remove(itemsCarried,json.indexOf(itemsCarried,readiedID))]
    [h: setProperty("shieldDB",getProperty("shieldDB")+readiedShieldDB)]
    [r:"readies " + msg]
  };{
    [h:itemsCarried=json.append(itemsCarried,readiedID)]
    [r:"picks up " + msg]
  }]
}]

[macro("calcEnc@"+ getMacroLocation()):""]
[if(json.contains(itemReadied,"armor")),h, code: {[macro("calcDR@"+ getMacroLocation()):""]};{""}]
[MACRO("CharSheet@" + getMacroLocation()): "tab=Inventory"]

[h:itemsCarried=getProperty("itemsCarried")]
[h:DRtotal=getProperty("DR")]
[h:locationList=json.fields(DRtotal)]

[foreach(location, locationList), h, code:{
    [h:DRtotal=json.set(DRtotal, location, 0)]
}]

<!-- Convert the readied iterms' ID numbers into their current indexes in the inventory list  --> 
[h:itemsReadiedByIndex=""]
[foreach(readiedID, itemsReadied,""), h,code: { 
  [h:matchingIndex=-1]
  [while (matchingIndex==-1,"" ), h, code: {
    [h: currItemCarried = json.get (inventory,roll.count)]
    [if (json.get(currItemCarried,"ID") == readiedID), h: matchingIndex= roll.count]
  }]
  [h:itemsReadiedByIndex=json.append(itemsReadiedByIndex,matchingIndex)]
}] 

[r:itemsReadiedByIndex] ->

<!-- Create a list containing only armor items -->
[h:armorReadiedByIndex=""]
[foreach(readiedItemIndex, itemsReadiedByIndex,""), r,code: { 
  [h:currItem=json.get(inventory,readiedItemIndex)]
  [if(json.contains(currItem,"armor")),h:armorReadiedByIndex = json.append(armorReadiedByIndex, readiedItemIndex)]
}]

[r:armorReadiedByIndex]

<!-- Loop throught the list of readied armor items, adding up the DRs for each location. -->
[foreach(itemIndex, armorReadiedByIndex,""), h, code: {
     [foreach(location, locationList), r, code: {
        [h:newDRAtLocation=json.get(DRtotal,location)+json.get(json.get(inventory,itemIndex),location)]
        [h:DRtotal=json.set(DRtotal, location, newDRAtLocation)]
     }]
}]

<!-- Loop throught the locations, adding in the natural DRs for each location. -->
[foreach(location, locationList), r, code: {
     [h:newDRAtLocation=json.get(DRtotal,location)+json.get(DRNatural,location)]
     [h:DRtotal=json.set(DRtotal, location, newDRAtLocation)]
}]

[h:DRtotal=setProperty("DR",DRtotal)]

[h: args = macro.args]
[if(json.isEmpty(args)),h,code: {
   [h:dialogSetup="delta | 0 | change in FP"]
   [h:dialog=input(dialogSetup)]
   [h:abort(dialog)]
   [h:nameIfneeded=""]
};{
   [h:delta=json.get(args,"drain")]
   [switchToken(json.get(args,"targetID"))]
   [h:nameIfneeded=token.name +" "]
}]

[h: FP=FP+delta]
[h, if(FP>maxFP):FP=maxFP;""]
[h, if(FP<-1*maxFP):FP=-1*maxFP]

[h, if(FP==maxFP), code:{ 
  [h:state.fresh=1]
  [h:setProperty("colorFP","green")]
};{
  [h:state.fresh=0]
}]

[h, if(FP>=(maxFP/3) && FP < maxFP), code:{ 
  [h:state.tiring=1]
  [h:setProperty("colorFP","yellow")]
};{
  [h:state.tiring=0]
}]

[h, if(FP>0 && FP<(maxFP/3)), code:{ 
  [h:state.exhausted=1]
  [h:setProperty("colorFP","orange")]
};{
  [h:state.exhausted=0]
}]

[h, if(FP <= 0), code:{ 
  [h:state.collapsing=1]
  [h:setProperty("colorFP","red")]
};{
  [h:state.collapsing=0]
}]

[h, if(FP < maxFP/3 || HP < maxHP/3):state.halfmove=1;state.halfmove=0]
[h, if(FP == -1*maxFP): state.unconscious=1;state.unconscious=0]

<!-- Generate and display output. -->
[h:output=nameIfNeeded]
[h, if(delta>0): output=output+"regains "; output=output+"receives "]
[h:output=output + delta + " fatigue, and now has " + FP + " FP"]

[h,if(state.halfmove==1): output=output + ": at half move";""]
[h, if(state.unconscious==1): output=output + " and unconscious from exhaustion";""]

[if(isNPC()),code:{
   [r,g:output + "."]
};{
   [r,s,g:output + "."]
}]

[MACRO("CharSheet@" + getMacroLocation()): "tab=Main"]
[h, if(isGM()),code: {
  [macro("initFrame@this"):""]
};{}]

[h: args = macro.args]
[if(json.isEmpty(args)),h,code: {
   [h:dialogSetup="delta | 0 | change in HP"]
   [h:dialog=input(dialogSetup)]
   [h:abort(dialog)]
   [h:nameIfneeded=""]
};{
   [h:delta=json.get(args,"injury")]
   [switchToken(json.get(args,"targetID"))]
   [h:targetName=getName(json.get(args,"targetID"))]
   [h:targetNameLower=lower(targetName)]
   [if( matches( targetName, targetNameLower) ),h: article="The" ; article =""]
   [h:nameIfneeded=article + " " + token.name +" "]
}]

[h:oldHP=getProperty("HP")]
[h:maxHP=getProperty("maxHP")]

[h: HP=oldHP+delta]
[h, if(HP>maxHP):HP=maxHP]
[h, if(HP<-10*maxHP):HP=-10*maxHP]

[h: deathRollMsg=""]
[h,if(HP>(-1*HP)), code:{
  [h:multiplierList=""]
};{
  [h:multiplierList="-1,-2,-3,-4"]
}]

[h:modHT=getProperty("HTBonus")+ getProperty("HTDeathBonus")]
[h, foreach (multiplier, multiplierList), code:{
  [h:HPlevel=multiplier * maxHP]
  [h, if( oldHP > HPlevel &&  HP <= HPlevel ), code : {
    [h: deathRollMsg=deathRollMsg+ "<br>"+token.name + " passes " + multiplier*maxHP + " HP.  Roll against modified HT to avoid dying."]
  };{}]
}]

[h, if(HP==maxHP), code:{ 
  [h:state.healthy=1]
  [h:setProperty("colorHP","green")]
};{
  [h:state.healthy=0]
}]

[h, if(HP>=(maxHP/3) && HP < maxHP), code:{ 
  [h:state.injured=1]
  [h:setProperty("colorHP","yellow")]
};{
  [h:state.injured=0]
}]

[h, if(HP>0 && HP<(maxHP/3)), code:{ 
  [h:state.maimed=1]
  [h:setProperty("colorHP","orange")]
};{
  [h:state.maimed=0]
}]

[h, if(HP <= 0), code:{ 
  [h:state.dying=1]
  [h:setProperty("colorHP","red")]
};{
  [h:state.dying=0]
}]

[h, if(HP <= -1*maxHP*5): state.dead = 1;state.dead = 0]
[h, if(FP < maxFP/3 || HP < maxHP/3):state.halfmove =1;state.halfmove=0]

<!-- Generate and display output. -->
[h:output=nameIfNeeded]
[h, if(delta>0): output=output+"regains "; output=output+"receives "]
[h:output=output + delta + " HP, and now has " + HP + " HP"]

[h,if(state.halfmove==1): output=output + ": at half move";""]
[h, if(state.dead==1): output=output + " and has died from <i>fearful</i> wounds."; output=output+".  "]

[if(isNPC()),code:{ 
    [r,g:output]
    [r,g:deathRollMsg]
};{
    [r:output]
    [r,g,s:deathRollMsg]
} ]

[MACRO("CharSheet@" + getMacroLocation()): "tab=Main"]
[h, if(isGM()),code: {
  [macro("initFrame@this"):""]
};{}]

[h:args=json.fromStrProp(macro.args)]
[h: skillName = json.get(args, "name")]
[h: skillLevel = json.get(args, "level")]
[h: skillCheckReason = json.get(args, "reason")]
[h, if (json.contains(args, "baseAttribute")): skillBaseAttribute = json.get(args, "baseAttribute"); skillBaseAttribute = ""]
[h: skillMod = json.get(args, "modifier")]

[if(skillMod == "ask" || skillMod == ""),h,code: {
   [h:dialogSetup="skillMod | 0 | "+skillName + " modifier"]
   [h:dialog=input(dialogSetup)]
   [h:abort(dialog)]
   };{}]

[h, if (skillName == "DX" || skillName == "IQ" || skillBaseAttribute == "DX" || skillBaseAttribute == "IQ") : skillMod = skillMod+getProperty("shock"); ""]

[h:effSkill=skillLevel+skillMod]
[h:roll=3d6]
[h: margin= effSkill - roll]

<!--Determine results -->
[h:effSkill=skillLevel+skillMod]
[h:resultMsg="rolls a " + roll + " against a base "+ skillName +" of "+ skillLevel +" with a modifier of "+ skillMod + "."]
[h, if( skillCheckReason ==""): "";resultMsg="check against " + skillCheckReason +": "+ resultMsg]

<!--Output results.  All NPC results are sent to the GM.  For stealth, search, observation, and Per, results are sent to the GM, and an announcement is sent to the player. -->

[if(isNPC() || skillName == "stealth" || skillName == "search" || skillName == "observation" || skillName == "Per"),code:{
   [gm:resultMsg]
};{
   [if( skillName == "stealth" || skillName == "search" || skillName == "observation" || skillName == "Per"),code:{
     [r,self: "A " + skillName + " check sent to GM."]
     [r,gm:resultMsg]
   };{
     [r:resultMsg]
   }]
}]

[h: indexChosen=macro.args]
[h: setProperty( "skills",json.remove(skills,indexChosen) )]

[h,if(getName()!="Lib:GURPS"), code:{
  [MACRO("CharSheet@" + getMacroLocation()): "tab=Skills"]
};{
  [MACRO("prefs@" + getMacroLocation()):""]
}]

[h:arg=macro.args]
[h:args=json.fromStrProp(macro.args)]

[h:propName=json.get(args,"property")] 
[h:propArrayModified=getProperty(propName)]
[h:indexObjMoved=json.get(args,"indexObjMoved")]
[h:indexObjDisplaced=json.get(args,"indexObjMoved")-1]

[h:objMoved=json.get(propArrayModified, indexObjMoved)]
[h:objDisplaced=json.get(propArrayModified, indexObjMoved-1)]
[h:propArrayModified=json.set(propArrayModified, indexObjMoved-1, objMoved, indexObjMoved, objDisplaced)]
[h:setProperty(propName,propArrayModified)]


[h,if(getName()!="Lib:GURPS"), code:{
  [h:origin=propName]
};{
  [h:origin="prefs"]
}]

[switch(origin), h, code :
  case "inventory" : {
    [MACRO("CharSheet@" + getMacroLocation()): "tab=Inventory"]
  };
  case "skills" : {
    [MACRO("CharSheet@" + getMacroLocation()): "tab=Skills"]
