  };
  case "prefs" : {
    [MACRO("prefs@" + getMacroLocation()): ""]
  };
  default : {
    ""
  }]

[h: nextInitiative()]

[if( isGM() ),h,code:{
  [macro("initFrame@Lib:GURPS"):""]
};{}]
[h:selectTokens(getInitiativeToken())]

[if(1==0), h, code : {
